/*eslint max-len: ["error", { "code": 200 }]*/

import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import base from '../../config/api.js';
let api = base.api();


class Data extends Component {
    static propTypes = {
        match: PropTypes.object.isRequired,
        location: PropTypes.object.isRequired,
        history: PropTypes.object.isRequired
    };
    constructor(props) {
        super(props);
        this.getContent = this.getContent.bind(this);
        this.setPaginate = this.setPaginate.bind(this);
        this.setCurrent = this.setCurrent.bind(this);
        this.setFilter1 = this.setFilter1.bind(this);
        this.setFilter2 = this.setFilter2.bind(this);
        this.getSearch = this.getSearch.bind(this);
        this.state = {
            type: this.props.type,
            title: "",
            data: [],
            search: [],
            showing1: false,
            showing2: false,
            pagination: [],
            filter1: "",
            filter2: "",
            current: 1,
            page: 1000
        };
    }

    componentDidMount() {
        this.getContent(this.props.type, this.state.current, this.state.page);
    }

    setPaginate(length) {
        let pageCount = Math.ceil(length / this.state.page);
        let pagination = [];

        for (let i = 1; i <= pageCount; i++) {
            let link = "#";
            pagination.push(
                <a href={link} className="paginate" onClick={() => this.setCurrent(i)}>{i}</a>
            )
        }

        this.setState({
            pagination: pagination
        });
    }

    setCurrent(current) {
        this.setState({
            current: current
        }, () => this.getContent(this.state.type, this.state.current, this.state.page));
    }

    setFilter1(e) {
        this.setState({
            filter1: e.target.value
        }, () => this.getContent(this.state.type, this.state.current, this.state.page));
    }

    setFilter2(e) {
        this.setState({
            filter2: e.target.value
        }, () => this.getContent(this.state.type, this.state.current, this.state.page));
    }

    getSearch(toggle1, toggle2=false) {
        this.setState({
            showing1: toggle1,
            showing2: toggle2
        });
    }

    getContent(type, current, page) {
        let that = this;
        let filter1 = this.state.filter1;
        let filter2 = this.state.filter2;
        let title;
        let url;

        switch (type) {
            case "all":
                url = "/data/";
                this.getSearch(false);
                details = {
                    title: "View All"
                }
                break;

            case "ip":
                url = "/data?ip=" + filter1;
                this.getSearch(true);
                details = {
                    title: "Filter IP"
                }
                break;

            case "url":
                url = "/data?url=" + filter1;
                this.getSearch(true);
                details = {
                    title: "Filter URL"
                }
                break;

            case "day":
                url = "/data?day=" + filter1;
                this.getSearch(true);
                details = {
                    title: "Search Day"
                }
                break;

            case "month":
                url = "/data?month=" + filter1;
                this.getSearch(true);
                details = {
                    title: "Search Month"
                }
                break;

            case "time":
                url = "/data?time=" + filter1;
                this.getSearch(true);
                details = {
                    title: "Search Time"
                }
                break;

            case "day-time":
                url = "/data?day=" + filter1 + "&time=" +filter2;
                this.getSearch(true, true);
                details = {
                    title: "Search Day & Time"
                }
                break;

            default:
                url = "/data/";
                this.getSearch(false);
        }

        this.setState({
            title: title
        });

        if (url && this.state.res) {
            this.makeTable(this.state.res, current, page)
        } else if (url) {
            fetch(api + url)
                .then(res => res.json())
                .then(res => this.makeTable(res, current, page));
        }
    }

    makeTable(data, current, page) {
        let result = [];
        let start = (current * page) - page;
        let stop = (start + page) - 1;
        let dataCount = data.length;

        this.setPaginate(dataCount);

        for (let i = start; i <= stop; i++) {
            if (i === dataCount) {
                break;
            } else {
                result.push(
                    <tr key={i}>
                        <td width="20%">{ data[i].ip }</td>
                        <td width="40%">{ data[i].url }</td>
                        <td width="20%">{ data[i].day + " " + data[i].month + " " + data[i].year }</td>
                        <td width="20%">{ data[i].time }</td>
                    </tr>
                )
            }
        }

        this.setState({
            data: [
                <table className="fixed-table">
                    <tr>
                        <th>IP</th>
                        <th>URL</th>
                        <th>Date</th>
                        <th>Time</th>
                    </tr>
                    { result }
                </table>
            ]
        });
    }

    render() {
        const { showing1 } = this.state;
        const { showing2 } = this.state;
        return (
            <article>
                <h2 className="center">{this.state.title}</h2>
                { showing1
                    ?   <input
                            className="form-input"
                            type="text"
                            name="filter1"
                            placeholder={this.state.type}
                            value={this.state.filter1}
                            onChange={this.setFilter1}
                        />
                    :null
                }
                { showing2
                    ?   <input
                            className="form-input"
                            type="text"
                            name="filter2"
                            placeholder={this.state.type}
                            value={this.state.filter2}
                            onChange={this.setFilter2}
                        />
                    :null
                }
                { this.state.pagination }
                { this.state.data }
            </article>
        );
    }
}

export default Data;
