/*eslint max-len: ["error", { "code": 150 }]*/

import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import base from '../../config/api.js';
let api = base.api();


class Data extends Component {
    static propTypes = {
        match: PropTypes.object.isRequired,
        location: PropTypes.object.isRequired,
        history: PropTypes.object.isRequired
    };
    constructor(props) {
        super(props);
        this.getContent = this.getContent.bind(this);
        this.setPaginate = this.setPaginate.bind(this);
        this.setCurrent = this.setCurrent.bind(this);
        this.state = {
            type: this.props.type,
            data: [],
            search: [],
            pagination: [],
            filter: "",
            current: 1,
            page: 1000
        };
    }

    componentDidMount() {
        this.getContent(this.props.type, this.state.current, this.state.page);
    }

    setPaginate(length) {
        let pageCount = Math.ceil(length / this.state.page);
        let pagination = [];

        for (let i = 1; i <= pageCount; i++) {
            let link = "#";
            pagination.push(
                <a href={link} className="paginate" onClick={() => this.setCurrent(i)}>{i}</a>
            )
        }

        this.setState({
            pagination: pagination
        });
    }

    setCurrent(current) {
        this.setState({
            current: current
        }, () => this.getContent(this.state.type, this.state.current, this.state.page));
    }

    getSearch() {
        this.setState({
            search: [
                <label className="form-label" key="search">Filter
                    <input className="form-input" type="text" name="filter" placeholder="filter" />
                </label>
            ]
        });
    }

    getContent(type, current, page) {
        let that = this;
        let filter = this.state.filter;
        let url;

        switch (type) {
            case "all":
                url = "/data/";
                break;

            case "ip":
                url = "/data?ip=" + filter;
                this.getSearch();
                break;

            case "url":
                url = "/data?url=" + filter;
                this.getSearch();
                break;

            default:
                this.setState({
                    data: [
                        <p>Start</p>
                    ]
                });
        }
        if (url && this.state.res) {
            this.makeTable(this.state.res, current, page)
        } else if (url) {
            fetch(api + url)
                .then(res => res.json())
                .then(res => this.makeTable(res, current, page));
        }
    }

    makeTable(data, current, page) {
        let result = [];
        let start = (current * page) - page;
        let stop = (start + page) - 1;
        let dataCount = data.length;

        this.setPaginate(dataCount);

        for (let i = start; i <= stop; i++) {
            if (i === dataCount) {
                break;
            } else {
                result.push(
                    <tr key={i}>
                        <td width="20%">{ data[i].ip }</td>
                        <td width="40%">{ data[i].url }</td>
                        <td width="20%">{ data[i].day + " " + data[i].month + " " + data[i].year }</td>
                        <td width="20%">{ data[i].time }</td>
                    </tr>
                )
            }
        }

        this.setState({
            data: [
                <table className="fixed-table">
                    <tr>
                        <th>IP</th>
                        <th>URL</th>
                        <th>Date</th>
                        <th>Time</th>
                    </tr>
                    { result }
                </table>
            ]
        });
    }

    render() {
        return (
            <article>
                { this.state.pagination }
                { this.state.search }
                { this.state.data }
            </article>
        );
    }
}

export default Data;
