                <p>{ this.state.para2 }</p>
                <p>{ this.state.para3 }</p>
                <div className="backdrop"><img src={meBackdrop} className="Manchester" alt="Manchester" />St Peter&apos;s Square, Manchester</div>
            </article>
        );
    }
}

export default Me;
