/*eslint max-len: ["error", { "code": 150 }]*/

import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import PropTypes from 'prop-types';
import base from '../../config/api.js';
let api = base.api();


class Data extends Component {
    static propTypes = {
        match: PropTypes.object.isRequired,
        location: PropTypes.object.isRequired,
        history: PropTypes.object.isRequired
    };
    constructor(props) {
        super(props);
        this.getContent = this.getContent.bind(this);
        this.state = {
            type: this.props.type,
            data: [],
            current: 1,
            page: 1000,
            pagination: ""
        };
    }

    componentDidMount() {
        this.getContent(this.props.type);
    }

    setPaginate(length) {
        let pageCount = Math.ceil(length / this.state.page);
        let pagination = [];

        for (let i = 1; i <= pageCount; i++) {
            let link = "?page=" + i;
            pagination.push(
                <a href={link} className="paginate" onClick={() => this.setCurrent(i)}>{i}</a>
            )
        }

        this.setState({
            pagination: pagination
        });
    }

    setCurrent(current) {
        this.setState({
            current: current
        });
        this.getContent(this.state.type);
    }

    getContent(type) {
        let that = this;
        let filter;

        switch (type) {
            case "all":
                filter = "/data/";
                break;

            case "ip":
                filter = "/data?ip=";
                break;

            case "url":
                filter = "/data?url=";
                break;

            default:
                this.setState({
                    data: [
                        <p>Start</p>
                    ]
                });
        }

        if (filter) {
            fetch(api + filter)
                .then(res => res.json())
                .then(res => this.makeTable(res))
                .then(function (res) {
                    that.setState({
                        res: res
                    });
                });
        }
    }

    makeTable(data) {
        let result = [];
        let page = this.state.page;
        let start = this.state.current - 1;
        let stop = this.state.current + this.state.page

        this.setPaginate(data.length);

        for (let i = start; i <= stop; i++) {
            result.push(
                <tr key={data[i].ip}>
                    <td width="20%">{ data[i].ip }</td>
                    <td width="40%">{ data[i].url }</td>
                    <td width="20%">{ data[i].day + " " + data[i].month + " " + data[i].year }</td>
                    <td width="20%">{ data[i].time }</td>
                </tr>
            )
        }

        this.setState({
            data: [
                <table className="fixed-table">
                    <tr>
                        <th>IP</th>
                        <th>URL</th>
                        <th>Date</th>
                        <th>Time</th>
                    </tr>
                    { result }
                </table>
            ]
        });
    }

    render() {
        return (
            <article>
                { this.state.pagination }
                { this.state.data }
            </article>
        );
    }
}

export default Data;
