/*eslint max-len: ["error", { "code": 200 }]*/

import React, { Component } from 'react';
import log from '../../assets/img/log.png';
import base from '../../config/api.js';
let url = base.api();

class Server extends Component {
    constructor(props) {
        super(props);
        this.state = {
            current: url
        };
    }

    changeServer(e) {
        const data = new FormData(e.target);
        localStorage.setItem("server", data.get("server"));
    }
    render() {
        return (
            <article>
                <h1>Server URL</h1>
                <h4 className="center">The current server url is: {this.state.current}</h4>
                <div className="form-wrapper">
                    <form action="/profile" className="form-register" onSubmit={this.changeServer}>
                        <label className="form-label">Server
                            <select className="form-input" name="server" required>
                                <option>localhost</option>
                                <option>server</option>
                            </select>
                        </label>
                        <input className="button form-button center" type="submit" name="submit" value="Submit" />
                    </form>
                </div>
            </article>
        );
    }
}

export default Server;
